create table ChatBlock (
         id bigint not null auto_increment,
         sourceUserEntityId bigint not null,
         targetUserEntityId bigint not null,
         primary key (id)
       );

create table ChatClosedConvo (
         id bigint not null auto_increment,
         sourceUserEntityId bigint not null,
         targetUserEntityId bigint not null,
         primary key (id)
       );
